import React from 'react';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      counter: 0
    }
  }
  hanldeIncrease = () => {
    this.setState({counter: this.state.counter + 1})
  }
  render(){
  return (
    <div>
      <div>{this.state.counter}</div>
      <div><button onClick={this.hanldeIncrease}>+</button></div>
    </div>
  )
}}



export default App;
