import React from 'react';
import Radio from './checkedRadio';

import "./App.css";

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      data: [ 
        {name: "Zlex", birthday: "02/09/1981"},
        {name: "Cecllia", birthday: "09/16/1993"},
        {name: "Deta", birthday: "10/31/1999"},
        {name: "John", birthday: "12/01/1982"},
        {name: "Lina", birthday:"01/16/2011"},
        {name: "Wallen", birthday:"11/30/1986"}],
      sort: null
    }
  }
  handleSort = e => {
    console.log(e);
    e.preventDefault();
    let type = e.target.value;
    this.setState({...this.state, sort: e.target.value});
  }
  render(){
    const sort=this.state.sort;
    console.log(this.state.sort);
  return (
    <div className="App">
      <div>
        <div>
          <h2>Birthday Records</h2>
        </div>
        <label>
          <input
            type="radio"
            value="name"
            checked={sort==="name"}
            onChange={this.handleSort}
          />
          name
        </label>
        <label>
          <input 
            type="radio"
            value="age"
            checked={sort==="age"}
            onChange={this.handleSort}
          />
          age
        </label>
        <table>
          <thead>
            <tr>
              <th>
                Person Name
              </th>
              <th>
                Date of Birth
              </th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.data.map((item, index) => {
                return(
                  <tr key={index}>
                    <td>{item.name}</td>
                    <td>{item.birthday}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
    </div>
  )
}}

export default App;
