import React from 'react';
import "./App.css";
class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      data: [ 
        {name: "Zlex", birthday: "02/09/1981"},
        {name: "Cecllia", birthday: "09/16/1993"},
        {name: "Deta", birthday: "10/31/1999"},
        {name: "John", birthday: "12/01/1982"},
        {name: "Lina", birthday:"01/16/2011"},
        {name: "Wallen", birthday:"11/30/1986"}],
      name: false,
      age: false
    }
  }
  handleNameSort = () => {
    let CurrentData = this.state.data.sort((a, b) => a.name > b.name);
    this.setState({...this.state, data: CurrentData, name: true, age: false});
  }
  handleAgeSort = () => {
    let tmp = this.state.data.sort((a, b) => a.age > b.age);
    this.setState({...this.state, data: tmp, name: false, age: true});
  }
  render(){
  return (
    <div className="App">
      <div>
        <div>
          <h2>Birthday Records</h2>
        </div>
        <form onChange={this.handleNameSort}>
        <div>
          <input type="radio" value="name" onClick={this.handleNameSort}/>
            Sort by name
          <input type="radio" value="age" onClick={this.handleAgeSort}/>
            Sort by age
        </div>
        </form>
        <table>
          <thead>
            <tr>
              <th>
                Person Name
              </th>
              <th>
                Date of Birth
              </th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.data.map((item, index) => {
                return(
                  <tr key={index}>
                    <td>{item.name}</td>
                    <td>{item.birthday}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
    </div>
  )
}}

export default App;
