import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      avatar: ''
    }
  }
  handleImage = e => {
    console.log(e.target.file)
    this.setState({avatar:e.target.value})
  }
  render(){
  return (
    <div className="App">
      <img src={logo} alt="not"/>
      <input 
        type="file"
        accept="image/jpeg"
        onChange={
          this.handleImage
        }
      />
    </div>
  )}
}

export default App;
