import React from 'react';
import './App.css';
import avatar1 from './avatar1.jpg';
import avatar2 from './avatar2.jpeg';
import avatar3 from './avatar3.jpeg';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      avatar: avatar3
    }
  }
  handleImage = e => {
    switch(e.target.value){
      case "C:\\fakepath\\avatar1.jpg":
        this.setState({avatar: avatar1});
        break;
      case "C:\\fakepath\\avatar2.jpeg":
        this.setState({avatar: avatar2});
        break;
      default:
        this.setState({avatar: avatar3});
    }
  }
  render(){
  return (
    <form>
        <label>
          Is going:
          <input
            name="isGoing"
            type="checkbox"
            checked={this.state.isGoing}
            onChange={this.handleInputChange} />
        </label>
        <br />
        <label>
          Number of guests:
          <input
            name="numberOfGuests"
            type="number"
            value={this.state.numberOfGuests}
            onChange={this.handleInputChange} />
        </label>
      </form>
  )}
}

export default App;
