import React from 'react';
import "./App.css";
import e from 'express';
class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      data: [ 
        {name: "Alex", birthday: "02/09/1981"},
        {name:"Cecllia", birthday: "09/16/1993"},
        {name: "Deta", birthday: "10/31/1999"},
        {name: "John", birthday: "12/01/1982"},
        {name: "Lina", birthday:"01/16/2011"},
        {name: "Wallen", birthday:"11/30/1986"}]
    }
  }
  handleNameSort = e => {
    e.preventDefault();
    let CurrentData = this.state.data.sort((a, b) => a.name > b.name);
    this.setState({data: CurrentData});
  }

  render(){
  return (
    <div className="App">
      <div>
        <div>
          <h2>Birthday Records</h2>
        </div>
        <div>
          <input type="radio" value="name" onClick={this.handleNameSort}/>
            Sort by name
          <input type="radio" value="age" onClick={this.handleAgeSort}/>
            Sort by age
        </div>
        <table>
          <thead>
            <tr>
              <th>
                Person Name
              </th>
              <th>
                Date of Birth
              </th>
            </tr>
          </thead>
          <tbody>
            {
              this.state.data.map((item, index) => {
                return(
                  <tr>
                    <td>{item.name}</td>
                    <td>{item.birthday}</td>
                  </tr>
                )
              })
            }
          </tbody>
        </table>
      </div>
    </div>
  )
}}



export default App;
