import React from 'react';
import './App.css';
import avatar1 from './avatar1.jpg';
import avatar2 from './avatar2.jpeg';
import avatar3 from './avatar3.jpeg';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      avatar: avatar3
    }
  }
  handleImage = e => {
    switch(e.target.value){
      case "C:\\fakepath\\avatar1.jpg":
        this.setState({avatar: avatar1});
        break;
      case "C:\\fakepath\\avatar2.jpeg":
        this.setState({avatar: avatar2});
        break;
      default:
        this.setState({avatar: avatar3});
    }
  }
  render(){
  return (
    <form>
         <div>
            <label > <input type="radio" name='gender' value="Man"
                onChange={this.handleChange}/>Man</label><br/>
            <label > <input type="radio" name='gender' value="Women"
                onChange={this.handleChange}/>Women</label>
            <div>gender: {this.state.value}</div>
          </div>
    </form>
  )}
}

export default App;
